const headers = {
    'User-Agent': 'MiFit/4.6.0 (iPhone; iOS 14.0.1; Scale/2.00)',
    'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
}

const axiosInstance = axios.create({
    baseURL: '',
    timeout: 20000
})
axiosInstance.interceptors.request.use(
    config => {
        return config
    },
    error => {
        console.log(error)
        return Promise.reject(error)
    }
)
axiosInstance.interceptors.response.use(
    response => {
        return response
    },
    error => {
        const {status} = error.response
        if (status >= 300 && status <= 399) {
            return error.response
        }
        // console.log(error)
        return Promise.reject(error)
    }
)

function random(min, max) {
    return Math.floor(Math.random() * (max - min)) + min;
}

async function getAccessCode(account, password) {
    const url = `https://api-user.huami.com/registrations/+86${account}/tokens`
    const params = {
        client_id: 'HuaMi',
        password: password,
        redirect_uri: 'https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html',
        token: 'access'
    }
    const data = qs.stringify(params)
    const response = await axiosInstance({url, method: 'post', data, headers: headers, maxRedirects: 0})
    const location = response.headers['location'] || false
    const access = /(?<=access=).*?(?=&)/.exec(location)
    if (!access || !access[0]) return false
    return access[0]
}

async function login(account, password) {
    const accessCode = await getAccessCode(account, password)
    const url = `https://account.huami.com/v2/client/login`
    const params = {
        app_version: '4.6.0',
        code: accessCode,
        country_code: 'CN',
        device_id: '2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1',
        device_model: 'phone',
        grant_type: 'access_token',
        third_name: 'huami_phone',
        app_name: 'com.xiaomi.hm.health'
    }
    const data = qs.stringify(params)
    const response = await axiosInstance({url, method: 'post', data, headers: headers, maxRedirects: 0})
    const tokenInfo = response.data.token_info || ""
    return { userId: tokenInfo.user_id, loginToken: tokenInfo.login_token }
}

async function getAppToken(loginToken) {
    const url = `https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&login_token=${loginToken}&os_version=4.1.0`
    const response = await axiosInstance({url, method: 'get'})
    return response.data.token_info
}

